str = """"""

count_A = 0
count_B = 0

for i in range(2, len(str)+1):
    substr = 'A' * i
    count_A += str.count(substr)
    
for i in range(2, len(str)+1):
    substr = 'B' * i
    count_B += str.count(substr)
print(f"Number of times A appears: {str.count('A')}")
print(f"Number of times B appears: {str.count('B')}")
counts_A = {}

counts_B = {}
current_count = 0
current_letter = None

# iterate through the string, counting consecutive letter occurrences
for letter in str:
    if letter == current_letter:
        current_count += 1
    else:
        if current_letter is not None:
            if current_letter == "A":
                counts_A[current_letter * current_count] = counts_A.get(current_letter * current_count, 0) + 1
            elif current_letter == "B":
                counts_B[current_letter * current_count] = counts_B.get(current_letter * current_count, 0) + 1
        current_letter = letter
        current_count = 1

# count the final sequence of letters
if current_letter is not None:
    if current_letter == "A":
        counts_A[current_letter * current_count] = counts_A.get(current_letter * current_count, 0) + 1
    elif current_letter == "B":
        counts_B[current_letter * current_count] = counts_B.get(current_letter * current_count, 0) + 1

# print the counts of each letter combination
print("Counts of letter combinations ending with A:")
for key, value in counts_A.items():
    print(f"exact {len(key)}A,appers: {value}")
    #print(key, value)

print("\nCounts of letter combinations ending with B:")
for key, value in counts_B.items():
    print(f"exact {len(key)}B,appers: {value}")